CREATE TABLE BLOG_ITEM(INUMBER INTEGER GENERATED BY DEFAULT AS IDENTITY(START WITH 0) NOT NULL,ITITLE VARCHAR(160) DEFAULT NULL,IBODY VARCHAR(16777216) NOT NULL,IMORE VARCHAR(16777216),ITIME TIMESTAMP NOT NULL,CONSTRAINT BLOG_ITEM_PKEY PRIMARY KEY(INUMBER))
CREATE TABLE BLOG_CATEGORIES(ID INTEGER PRIMARY KEY,NAME VARCHAR(20) NOT NULL)
CREATE TABLE BLOG_ITEM_CATEGORIES(ITEM_ID INTEGER NOT NULL,CATEGORY_ID INTEGER NOT NULL,PRIMARY KEY(ITEM_ID,CATEGORY_ID),FOREIGN KEY(ITEM_ID) REFERENCES BLOG_ITEM(INUMBER),FOREIGN KEY(CATEGORY_ID) REFERENCES BLOG_CATEGORIES(ID))
